import { secondsToDuration } from "../utils/duration";

const DisplayBreak = ({breakDuration}) => {
    return <span className="input-group-text" data-testid="duration-break">Break Duration: {secondsToDuration(breakDuration)}</span>
}

export default DisplayBreak;
