const DisplayFocusTimer = ({focusTimerDuration}) => {
    const minutes = Math.floor(focusTimerDuration/60)
    const seconds = focusTimerDuration % 60;
    if (focusTimerDuration % 60<10) {
        return minutes+":0"+seconds;
    } if (Math.floor(focusTimerDuration/60) < 10) {
        return "0"+minutes+":"+seconds;
    } if (focusTimerDuration % 60<10 && Math.floor(focusTimerDuration/60) < 10) {
        return "0"+minutes+":0"+seconds;
    } else {
        return minutes+":"+seconds
    }
}

export default DisplayFocusTimer;
