const DisplayBreakTimer = ({breakTimerDuration}) => {
    const minutes = Math.floor(breakTimerDuration/60)
    const seconds = breakTimerDuration % 60;
    if (breakTimerDuration % 60===0 && Math.floor(breakTimerDuration/60) < 10) {
        return "0"+minutes+":0"+seconds;
    } else if (Math.floor(breakTimerDuration/60) < 10) {
        return "0"+minutes+":"+seconds;
    } else if (breakTimerDuration % 60===0) {
        return minutes+":0"+seconds;
    } else {
        return minutes+":"+seconds;
    }
}

export default DisplayBreakTimer;
