import { minutesToDuration } from "../utils/duration";
import React from "react";

const SessionTitle = ({focusDuration, breakDuration, onBreak}) => {
    return !onBreak ? (<h2 data-testid="session-title">Focusing for {secondsToDuration(focusDuration)} minutes</h2>) : (<h2 data-testid="session-title">On Break for {secondsToDuration(breakDuration)} minutes</h2>);
}

export default SessionTitle
