const DisplayFocusTimer = ({focusTimerDuration}) => {
    const minutes = Math.floor(focusTimerDuration/60)
    const seconds = focusTimerDuration % 60;
    return (focusTimerDuration % 60===0) ? minutes+":00" : minutes+":"+seconds
}

export default DisplayFocusTimer;
